function draw(){mainContext.beginPath(),mainContext.arc(150,150,100,0*Math.PI,2*Math.PI,!1),mainContext.lineWidth=10,mainContext.strokeStyle="rgba(53, 50, 56, 1.0)",mainContext.stroke(),mainContext.beginPath(),mainContext.arc(150,150,100,1.5*Math.PI,1*Math.PI,!1),mainContext.lineWidth=10,mainContext.strokeStyle="rgba(216, 0, 96, 1.0)",mainContext.stroke()}function convertMins(t){var n=Math.floor(t/60),e=t%60;return e<10?n+":0"+e:n+":"+e}function countDown(t){function n(){timeLeft-counter>0?(counterDom.innerHTML="<h3>"+convertMins(timeLeft-counter)+"</h3>",counter++):(rest(),audio.play())}timeLeft=6*t,setInterval(n,1e3)}function rest(){timeLeft=10,counter=0,timeLeft-counter>0?(counterDom.innerHTML="<h3>"+convertMins(timeLeft-counter)+"</h3><p>Rest</p>",counter++):(countDown(1),audio.play())}var mainCanvas=document.getElementById("myCanvas"),mainContext=mainCanvas.getContext("2d");draw();var counter=0,timeLeft,audio=new Audio("alarm.mp3"),counterDom=document.getElementById("counter"),button=document.getElementById("start-button");button.addEventListener("click",function(){countDown(1),"START"===button.innerHTML?button.innerHTML="STOP":button.innerHTML="START"});